import Card from '../Card'
import uwLogo from '../../resources/color-UWcrest-print.png'
import mePhoto from '../../resources/meAvatar.jpg'
import Router from "next/router"

export default class TopBarNav extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            cardHeight: 40,
            currentPage: this.props.currentPage,
        }
    }



    render() {
        var selectColor1;
        var selectColor2;
        var selectColor3;
        var selectColor4;

        if(Router.pathname== "/"){
            selectColor1 = "#C2DFFF"
            selectColor2 = "#000000"
            selectColor3 = "#000000"
            selectColor4 = "#000000"
        }
        if(Router.pathname== "/projects"){
            selectColor1 = "#000000"
            selectColor2 = "#C2DFFF"
            selectColor3 = "#000000"
            selectColor4 = "#000000"
        }
        if(Router.pathname== "/gallery"){
            selectColor1 = "#000000"
            selectColor2 = "#000000"
            selectColor3 = "#C2DFFF"
            selectColor4 = "#000000"
        }
        if(Router.pathname== "/contact"){
            selectColor1 = "#000000"
            selectColor2 = "#000000"
            selectColor3 = "#000000"
            selectColor4 = "#C2DFFF"
        }

        return (
            <div className='topbar'>

                {/* My name and wisconsin Image */}
                <Card clickable={false} height='100%' width='30vw'>
                    <img src={uwLogo} style={{ paddingLeft: 55, height: 70, maxWidth: 60 }}></img>
                    <text style={{ fontFamily: 'Trench Thin', fontSize: 50, paddingLeft: 20, flex: 1 }}>
                        Max Johnson
                </text>
                </Card>

                {/* Home Button */}
                
                <Card clickable={true} height={this.state.cardHeight} width='15vw' onClick={() => Router.push("/")}>
                    <text style={{ paddingTop: 17, fontSize: 40, fontFamily: 'Trench Thin', color: selectColor1 }}>
                        Home
                    </text>
                </Card>
                

                {/* Projects Button */}
                
                <Card clickable={true} height={this.state.cardHeight} width='15vw' onClick={() => Router.push("/projects")}>
                    <text style={{ paddingTop: 17, fontSize: 40, fontFamily: 'arial', color: selectColor2 }}>
                        Projects
                    </text>
                </Card>
                

                {/* Gallery Button */}
                
                <Card clickable={true} height={this.state.cardHeight} width='15vw' onClick={() => Router.push("/gallery")}>
                    <text style={{ paddingTop: 17, fontSize: 40, fontFamily: 'arial', color: selectColor3 }}>
                        Gallery
                    </text>
                </Card>
                

                {/** Contact Button */}
                
                <Card className='Card' clickable={true} height={this.state.cardHeight} width='15vw' onClick={() => Router.push("/contact")}>
                    <text style={{ paddingTop: 17, fontSize: 40, fontFamily: 'arial', color: selectColor4 }}>
                        Contact
                    </text>
                </Card>
                

                {/**picture of me */}
                <Card clickable={false} height={this.state.cardHeight} width='10vw'>
                    <img className="avatar" src={mePhoto} style={{ borderRadius: '50%', height: 50, width: 50, transform: 'translate(0,20px)', position: 'absolute' }}>
                    </img>
                </Card>

                <style jsx>{`
                .topbar {
                    margin:0;
                    padding:0;
                    border:0;
                    max-height:40;
                    max-width: 100%;
                    flex-direction: row;
                    display: flex;
                }
                

                @font-face{
                   font-family: "Trench Thin";
                   src: url('../../resources/TrenchThin-aZ1J.ttf');
                   font-weight: bold;
                   font-display: auto;
                   font-style: normal;
                }
            `}</style>
            </div>
        )
    }
}
