import Card from "../Card"

export default class ImageCard extends React.Component {
    constructor(props) {
        super(props);

        this.state={
            hover: false,
            height: this.props.height,
            width: this.props.width,
        }
    }

    render() {
        var imgStyle;
        const hoverHeight= this.state.height*1.1;
        const hoverWidth = this.state.width*1.1;
        if(this.state.hover){
            
            imgStyle={boxShadow: '0 4px 8px 0 rgba(0, 0, 0, 0.4), 0 6px 20px 0 rgba(0, 0, 0, 0.19)', transform: "translate(200px)", width: hoverWidth, height: hoverHeight}
        }else{
            imgStyle={boxShadow: '0 4px 8px 0 rgba(0, 0, 0, 0.4), 0 6px 20px 0 rgba(0, 0, 0, 0.19)', transform: "translate(200px)", width: this.state.width, height: this.state.height}
        }

        return (
            <Card width="auto" height='auto'>
               
                <img src={this.props.source} style={imgStyle} onMouseEnter={() => this.setState({ hover: true })} onMouseLeave={() => this.setState({ hover: false })}></img>
            </Card>
        )
    }
}
