export default class Card extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            clickable: this.props.clickable,   //If the card is clickable or not
            hover: false        //whether the card should hover or not
        }
    }

    render() {
        //checks to see if hover is true and which style to choose
        var linkStyle;
        if(this.state.hover){
            linkStyle={ width: '100%', height: this.props.height, background: '#F0F0F0', cursor: 'pointer', flexDirection: 'column', display: 'flex', alignItems: 'center', opacity: 1}
        }else{
            linkStyle={ width: '100%', height: this.props.height, background: '#E8E8E8', flexDirection: 'column', display: 'flex', alignItems: 'center', opacity: .4}
        }


        if (this.state.clickable) {
            //the Card that is created if clickable is true
            return (
                <div style={linkStyle} onMouseEnter={() => this.setState({hover: !this.state.hover})} onMouseLeave={() => this.setState({hover: !this.state.hover})} onClick={() => this.props.onClick}>
                    {this.props.children}
                </div>
            )

            //the card that is created if false
        } else {
            return (
                <div style={{ width: '100%', height: this.props.height, background: '#E8E8E8',flexDirection: 'column', display: 'flex', alignItems: 'center', objectFit:'contain'}}>
                    {this.props.children}
                </div>
            )
        }
    }
}
